import React, { useRef, useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import '../App.css';
import { Calendar } from 'primereact/calendar';
import { Button } from 'primereact/button';
import { Checkbox } from "primereact/checkbox";
 
export default function Edit() {
  const [form, setForm] = useState({
    title: "",
    author: "",
    date: "",
    genre: "",
    books: [],
 });

const params = useParams();
const navigate = useNavigate();

const [date, setDate] = useState('');
const dateInputRef = useRef(null);

const genreCategories = [
  { nam: 'Action & Adventure', key: 'AA'},
  { genre: 'Adult', key: 'A'}, 
  { genre: 'Bibliography', key: 'B'}, 
  { genre: 'Comedy', key: 'C'}, 
  { genre: 'Drama', key: 'D'}, 
  { genre: 'Dystopian', key: 'Dy'}, 
  { genre: 'Fantasy', key: 'F'}, 
  { genre: 'Mystery', key: 'M'}, 
  { genre: 'Romance', key: 'R'},
  { genre: 'Satire', key: 'S'},  
  { genre: 'Self Help', key: 'SH'},
  { genre: 'Thriller', key: 'T'},
  { genre: 'Young Adult', key: 'YA'}
];

const [selectedCategories, setSelectedCategories] = useState([genreCategories[1]]);

  const onCategoryChange = (e) => {
      let _selectedCategories = [...selectedCategories];

      if (e.checked)
          _selectedCategories.push(e.value);
      else
          _selectedCategories = _selectedCategories.filter(genreCategories => genreCategories.key !== e.value.key);

      setSelectedCategories(_selectedCategories);
  };
 
useEffect(() => {
  async function fetchData() {
    const id = params.id.toString();
    const response = await fetch(`http://localhost:3000/book/${params.id.toString()}`);
 
    if (!response.ok) {
      const message = `An error has occurred: ${response.statusText}`;
      window.alert(message);
      return;
    }
 
    const book = await response.json();
    if (!book) {
      window.alert(`book with id ${id} not found`);
      navigate("/");
      return;
    }
  setForm(book);
  }
 
  fetchData();
 
  return;
}, [params.id, navigate]);
 
 // These methods will update the state properties.
function updateForm(value) {
  return setForm((prev) => {
    return { ...prev, ...value };
  });
}
 
async function onSubmit(e) {
  e.preventDefault(); 

  var date = form.date;
    if (date == null) {
      date = 'Plan To Read';
    } else {
      date = JSON.stringify(date).slice(1, 11);
    }

   const editedBook = {
     title: form.title,
     author: form.author,
     date: date,
     genre: form.genre,
   };

   // This will send a post request to update the data in the database.
