import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
 
export default function Edit() {
 const [form, setForm] = useState({
   title: "",
   author: "",
   date: "",
   genre: "",
   records: [],
 });
 const params = useParams();
 const navigate = useNavigate();
 
 useEffect(() => {
   async function fetchData() {
     const id = params.id.toString();
     const response = await fetch(`http://localhost:3000/record/${params.id.toString()}`);
 
     if (!response.ok) {
       const message = `An error has occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }
 
     const record = await response.json();
     if (!record) {
       window.alert(`Record with id ${id} not found`);
       navigate("/");
       return;
     }
 
     setForm(record);
   }
 
   fetchData();
 
   return;
 }, [params.id, navigate]);
 
 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }
 
 async function onSubmit(e) {
   e.preventDefault();
   const editedPerson = {
     title: form.title,
     author: form.author,
     date: form.date,
     genre: form.genre,
   };
 
   // This will send a post request to update the data in the database.
   await fetch(`http://localhost:3000/update/${params.id}`, {
     method: "POST",
     body: JSON.stringify(editedPerson),
     headers: {
       'Content-Type': 'application/json'
     },
   });
 
   navigate("/");
 }
 
 // This following section will display the form that takes input from the user to update the data.
 return (
   <div>
     <h3>Update Record</h3>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="title">Title: </label>
         <input
           type="text"
           className="form-control"
           id="title"
           value={form.title}
           onChange={(e) => updateForm({ title: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="author">Author: </label>
         <input
           type="text"
           className="form-control"
           id="author"
           value={form.author}
           onChange={(e) => updateForm({ author: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="date">Date</label>
         <input
           type="text"
           className="form-control"
           id="date"
           value={form.date}
           onChange={(e) => updateForm({ date: e.target.value })}
         />
       </div>
       
       <div className="form-group">
          <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreAction&Adventure"
             value="Action&Adventure"
             checked={form.genre === "Action&Adventure"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreAction&Adventure" className="form-check-label">Action & Adventure</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreAdult"
             value="Adult"
             checked={form.genre === "Adult"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreAdult" className="form-check-label">Adult</label>
       </div>
       <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreBibliography"
             value="Bibliography"
             checked={form.genre === "Bibliography"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreBibliography" className="form-check-label">Bibliography</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreComedy"
             value="Comedy"
             checked={form.genre === "Comedy"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreComedy" className="form-check-label">Comedy</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreDystopian"
             value="Dystopian"
             checked={form.genre === "Dystopian"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreDystopian" className="form-check-label">Dystopian</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreFantasy"
             value="Fantasy"
             checked={form.genre === "Fantasy"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreFantasy" className="form-check-label">Fantasy</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreMystery"
             value="Mystery"
             checked={form.genre === "Mystery"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreMystery" className="form-check-label">Mystery</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreRomance"
             value="Romance"
             checked={form.genre === "Romance"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreRomance" className="form-check-label">Romance</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreSatire"
             value="Satire"
             checked={form.genre === "Satire"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreSatire" className="form-check-label">Satire</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreSatire"
             value="Thriller"
             checked={form.genre === "Thriller"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreThriller" className="form-check-label">Thriller</label>
         </div>
       </div>
       <br />
 
       <div className="form-group">
         <input
           type="submit"
           value="Update Book"
           className="btn btn-primary"
         />
       </div>
     </form>
   </div>
 );
}
