import React, { useRef, useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import '../App.css';
import { Calendar } from 'primereact/calendar';
import { Button } from 'primereact/button';
 
export default function Edit() {
 const [form, setForm] = useState({
   title: "",
   author: "",
   date: "",
   genre: "",
   books: [],
 });
 const params = useParams();
 const navigate = useNavigate();

 const [date, setDate] = useState('');
 const dateInputRef = useRef(null);
 
 useEffect(() => {
   async function fetchData() {
     const id = params.id.toString();
     const response = await fetch(`http://localhost:3000/book/${params.id.toString()}`);
 
     if (!response.ok) {
       const message = `An error has occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }
 
     const book = await response.json();
     if (!book) {
       window.alert(`book with id ${id} not found`);
       navigate("/");
       return;
     }
 
     setForm(book);
   }
 
   fetchData();
 
   return;
 }, [params.id, navigate]);
 
 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }
 
 async function onSubmit(e) {
   e.preventDefault(); 

    const date = form.date;
   if (date == null) {
    dateFormat = JSON.stringify(form.date).slice(1, 11);
   }

   const editedBook = {
     title: form.title,
     author: form.author,
     date: dateFormat,
     genre: form.genre,
   };
    console.log(editedBook)

   // This will send a post request to update the data in the database.
   await fetch(`http://localhost:3000/update/${params.id}`, {
     method: "POST",
     body: JSON.stringify(editedBook),
     headers: {
       'Content-Type': 'application/json'
     },
   });
 
   navigate("/");
 }
 
 // This following section will display the form that takes input from the user to update the data.
 return (
  <div>
    <i className="pi pi-fw pi-arrow-circle-left" onClick={() => navigate("/")} style={{'fontSize':'2em','cursor':'pointer','marginLeft':50}}></i>
   <div style={{ margin: 50, marginLeft: 200, marginRight:200 }}>
   <h3>Edit Book</h3>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="title">Title: </label>
         <input
           type="text"
           className="form-control"
           id="title"
           value={form.title}
           onChange={(e) => updateForm({ title: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="author">Author: </label>
         <input
           type="text"
           className="form-control"
           id="author"
           value={form.author}
           onChange={(e) => updateForm({ author: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="date">Date</label>
         <div>
          <Calendar 
              type="date"
              // className="form-control"
              id="calendar"
              value={form.date}
              onChange={(e) => updateForm({ date: e.target.value })}
              ref={dateInputRef}
              dateFormat="mm-dd-yy"
              showIcon 
              showButtonBar 
              touchUI 
              />
          </div>
       </div>
       
       <div className="">
          <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreAction&Adventure"
             value="Action & Adventure"
             checked={form.genre === "Action & Adventure"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreAction&Adventure" className="form-check-label">Action & Adventure</label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="genreOptions"
            id="genreAdult"
            value="Adult"
            checked={form.genre === "Adult"}
            onChange={(e) => updateForm({ genre: e.target.value })}
          />
          <label htmlFor="genreAdult" className="form-check-label">Adult</label>
       </div>
       <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreBibliography"
             value="Bibliography"
             checked={form.genre === "Bibliography"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreBibliography" className="form-check-label">Bibliography</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreComedy"
