import React, { useRef, useState } from "react";
import { useNavigate } from "react-router";
import '../App.css';
import { ConfirmDialog, confirmDialog } from 'primereact/confirmdialog';
import { Toast } from 'primereact/toast';
import { Button } from 'primereact/button';
 
export default function Create() {
 const [form, setForm] = useState({
    title: "",
    author: "",
    date: "",
    genre: "",
 });
 
 const navigate = useNavigate();

 const [visible, setVisible] = useState(false);
  const toast = useRef(null);

 const [date, setDate] = useState('');
 const dateInputRef = useRef(null);
 
const accept = () => {
        toast.current.show({ severity: 'info', summary: 'Confirmed', detail: 'Book deleted!', life: 2000 });
        setTimeout(() => {
            navigate("/")
        }, 2000);
    }

    const reject = () => {
        toast.current.show({ severity: 'warn', summary: 'Rejected', detail: 'You have rejected!', life: 2000 });
    }
 // These methods will update the state properties.
 function updateForm(value) {
   setDate(value);
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }
 
 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();
 
   // When a post request is sent to the create url, we'll add a new book to the database.
   const newPerson = { ...form };

   await fetch("http://localhost:3000/book/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });
 
   setForm({ title: "", author: "", date: "", genre: "" });
 }
 
 // This following section will display the form that takes the input from the user.
 return (
   <div style={{ margin: 100 }}>
     <h3>Add a New Book</h3>
     <form onSubmit={onSubmit}>
      
       <div className="form-group">
         <label htmlFor="title">Title</label>
         <input
           type="text"
           className="form-control"
           id="title"
           value={form.title}
           onChange={(e) => updateForm({ title: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="author">Author</label>
         <input
           type="text"
           className="form-control"
           id="author"
           value={form.author}
           onChange={(e) => updateForm({ author: e.target.value })}
         />
       </div>
 
       <div className="form-group">
         <label htmlFor="date">Date</label>
         <input
           type="date"
           className="form-control"
           id="date"
           value={form.date}
           onChange={(e) => updateForm({ date: e.target.value })}
           ref={dateInputRef}
         />
       </div>
       <div className="form-group">
       </div>
       <div className="form-group">
       <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreAction&Adventure"
             value="Action & Adventure"
             checked={form.genre === "Action & Adventure"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreAction&Adventure" className="form-check-label">Action & Adventure</label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="genreOptions"
            id="genreAdult"
            value="Adult"
            checked={form.genre === "Adult"}
            onChange={(e) => updateForm({ genre: e.target.value })}
          />
          <label htmlFor="genreAdult" className="form-check-label">Adult</label>
       </div>
       <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreBibliography"
             value="Bibliography"
             checked={form.genre === "Bibliography"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreBibliography" className="form-check-label">Bibliography</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreComedy"
             value="Comedy"
             checked={form.genre === "Comedy"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreComedy" className="form-check-label">Comedy</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreDrama"
             value="Drama"
             checked={form.genre === "Drama"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreDrama" className="form-check-label">Drama</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
