import React, { useState } from "react";
import { useNavigate } from "react-router";

import { Dayjs } from 'dayjs';
import TextField from '@mui/material/TextField';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker'
 
export default function Create() {
 const [form, setForm] = useState({
    title: "",
    author: "",
    date: "",
    genre: "",
 });
 
 const navigate = useNavigate();
 const [value, setValue] = React.useState<Dayjs | null>(null);
 
 // These methods will update the state properties.
 function updateForm(value) {
  console.log(value)
   return setForm((prev) => {
     return { ...prev, ...value };
   });
   
 }


 
 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();
 
   // When a post request is sent to the create url, we'll add a new record to the database.
   const newPerson = { ...form };

   await fetch("http://localhost:3000/record/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });
 
   setForm({ title: "", author: "", date: "", genre: "" });
   navigate("/");
 }
 
 // This following section will display the form that takes the input from the user.
 return (
   <div>
     <h3>Add a New Book</h3>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="title">Title</label>
         <input
           type="text"
           className="form-control"
           id="title"
           value={form.title}
           onChange={(e) => updateForm({ title: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="author">Author</label>
         <input
           type="text"
           className="form-control"
           id="author"
           value={form.author}
           onChange={(e) => updateForm({ author: e.target.value })}
         />
       </div>
       <LocalizationProvider dateAdapter={AdapterDayjs}>
        <DatePicker
          label="Basic example"
          value={value}
          onChange={(newValue) => {
            setValue(newValue);
          }}
          renderInput={(params) => <TextField {...params} />}
        />
      </LocalizationProvider>
       <div className="form-group">
         <label htmlFor="date">Date</label>
         <input
           type="text"
           className="form-control"
           id="date"
           value={form.date}
           onChange={(e) => updateForm({ date: e.target.value })}
         />
       </div>
       <div className="form-group">
       <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreAction&Adventure"
             value="Action&Adventure"
             checked={form.genre === "Action&Adventure"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreAction&Adventure" className="form-check-label">Action & Adventure</label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="genreOptions"
            id="genreAdult"
            value="Adult"
            checked={form.genre === "Adult"}
            onChange={(e) => updateForm({ genre: e.target.value })}
          />
          <label htmlFor="genreAdult" className="form-check-label">Adult</label>
       </div>
       <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreBibliography"
             value="Bibliography"
             checked={form.genre === "Bibliography"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreBibliography" className="form-check-label">Bibliography</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreComedy"
             value="Comedy"
             checked={form.genre === "Comedy"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreComedy" className="form-check-label">Comedy</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreDrama"
             value="Drama"
             checked={form.genre === "Drama"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreDrama" className="form-check-label">Drama</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreDystopian"
             value="Dystopian"
             checked={form.genre === "Dystopian"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreDystopian" className="form-check-label">Dystopian</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
             name="genreOptions"
             id="genreFantasy"
             value="Fantasy"
             checked={form.genre === "Fantasy"}
             onChange={(e) => updateForm({ genre: e.target.value })}
           />
           <label htmlFor="genreFantasy" className="form-check-label">Fantasy</label>
         </div>
         <div className="form-check form-check-inline">
           <input
             className="form-check-input"
             type="radio"
