//     title: 'Delete book',
//     message: `Are you sure you want to delete this book?`,
//     buttons: [
//       {
//         label: 'Cancel',
//       },
//       {
//         label: 'Delete',
//         onClick: () => {
//           deletingBook(id);
//         }
//       }
//     ]
//   })
