  customUI: ({ onClose }) => {
    return (
      <div className="react-confirm-alert-body">
        <h1>Remove {product.Title}</h1>
        <p>Are you sure want to remove this product from your cart?</p>
        <div className="react-confirm-alert-button-group">
          <button onClick={onClose}>No</button>
          <button id="btn-confirm-delete-cart"
            onClick={() => {
              this.props.removeFromCart(product.Id);
              onClose();
            }}
          >
            Yes, Remove it!
          </button>
        </div>
      </div>
    );
  }
});
 }


 async function deletingBook(id) {
  await fetch(`http://localhost:3000/${id}`, {
     method: "DELETE"
   });
 
   const newBooks = books.filter((el) => el._id !== id);
   setBooks(newBooks);
 }
 
 // This method will map out the records on the table
 function bookList() {

   return books.map((book) => {
     return (
       <Book
         book={book}
         deleteBook={() => deleteBook(book._id)}
         key={book._id}
       />
     );
   });
 }
 
 // This following section will display the table with the records of individuals.
 return (
   <div  style={{ margin: 100 }}>
     <h4> Total Books Read: {bookList().length}</h4>
     <h3 style={{ textAlign: "center", margin: 20}}>BOOK LIST</h3>
     <table className="table table-striped">
       <thead>
         <tr>
           <th>Title</th>
           <th>Author</th>
           <th>Date</th>
           <th>Genre</th>
           <th>Action</th>
         </tr>
       </thead>
       <tbody>{bookList()}</tbody>
     </table>
   </div>
 );
}
