import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router";
import Dropdown from 'react-bootstrap/Dropdown'
import '../App.css';

import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

// import { ConfirmPopup } from 'primereact/confirmpopup'; // To use <ConfirmPopup> tag
// import { confirmPopup } from 'primereact/confirmpopup'; // To use confirmPopup method
import { Menu } from 'primereact/menu';
import { Toast } from 'primereact/toast';
import { Menubar } from 'primereact/menubar';


const Book = (props) => (
  
 <tr>
   <td>{props.book.title}</td>
   <td>{props.book.author}</td>
   <td>{props.book.date}</td>
   <td>{props.book.genre}</td>
   <td>
   <Dropdown >
      <Dropdown.Toggle variant="success" id="dropdown-basic" style={{ background:"none", color:"black", border:"none"}}>
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item>
        <Link className="btn btn-link" to={`/edit/${props.book._id}`} style={{ textDecoration:"none"}} >Edit</Link>
        </Dropdown.Item>
        
        <Dropdown.Item>
          <Link className="btn btn-link" to={`/confirmation/${props.book._id}`} style={{ textDecoration:"none"}}>
            Delete
          </Link>
        </Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
   </td>
 </tr>
);

export default function BookList() {

  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const toast = useRef(null);
  const paginatorLeft = <Button type="button" icon="pi pi-refresh" className="p-button-text" />;
  const paginatorRight = <Button type="button" icon="pi pi-cloud" className="p-button-text" />;

  const onRowEditComplete = (e) => {
    let bookId = e.data._id
    navigate("edit/" + bookId);
  };

  const items = [
    {
      // label: 'Options',
      // items: [
      //   {
      //       label: 'Update',
      //       icon: 'pi pi-refresh',
      //       command: () => {
      //           toast.current.show({ severity: 'success', summary: 'Updated', detail: 'Data Updated', life: 3000 });
      //       }
      //   },
      //   {
      //       label: 'Delete',
      //       icon: 'pi pi-times',
      //       command: () => {
      //           toast.current.show({ severity: 'warn', summary: 'Delete', detail: 'Data Deleted', life: 3000 });
      //       }
      //   }
      // ]

      items: [
        {
            label: 'Edit',
            icon: 'pi pi-fw pi-pencil',
        },
        {
            label: 'Delete',
            icon: 'pi pi-fw pi-trash',
            items: [
              {
                  label: 'yes',
                  icon: 'pi pi-fw pi-calendar-plus'
              },
              {
                  label: 'No',
                  icon: 'pi pi-fw pi-calendar-minus'
              }
          ]
        }
      ]
    },
  ];

  const toggleClass = (e) => {
    let bookId = e._id

    // return <Dropdown >
    //         <Dropdown.Toggle variant="success" id="dropdown-basic" style={{ background:"none", color:"black", border:"none"}}></Dropdown.Toggle>

    //         <Dropdown.Menu>
    //           {/* <Dropdown.Item>
    //           <Link className="btn btn-link" to={`/edit/${e._id}`} style={{ textDecoration:"none"}} >Edit</Link>
    //           </Dropdown.Item>
              
    //           <Dropdown.Item>
    //             <Link className="btn btn-link" to={`/confirmation/${e._id}`} style={{ textDecoration:"none"}}>
    //               Delete
    //             </Link>
    //           </Dropdown.Item> */}
    
    //           {/* <div className="card flex justify-content-center">
    //               <Toast ref={toast} />
    //               <Menu model={items} />
    //           </div> */}
          
    //         </Dropdown.Menu>

    //       </Dropdown>
    return (
      <div className="card" style={{border:'none', width:50, marginRight:50}}>
        <Menubar model={items} />
      </div>
    )
  };

  // <Link className="btn btn-link" to={`/confirmation/${books._id}`} style={{ textDecoration:"none"}}>Delete</Link>
  // This method fetches the records from the database.
  useEffect(() => {
    async function getBooks() {
      const response = await fetch(`http://localhost:3000/book`);
  
      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }
      
      const books = await response.json();
      // order the books by date of completion
      const orderedBook = [...books].sort((a, b) => new Date(a.date) - new Date(b.date)).reverse();
      //  const orderedBook = [...books].sort((a, b) => a.title > b.title ? 1 : -1);
      setBooks(orderedBook);
    }
    getBooks();
 
    return;
}, [books.length]);
  
// This method will map out the records on the table
function bookList() {

  return books.map((book) => {
    return (
      <Book
        book={book}
        // deleteBook={() => deleteBook(book._id)}
        key={book._id}
      />
    );
  });
}
 
 // This following section will display the table with the records of individuals.
return (
  <div  style={{ margin: 'auto', width: '90%' }}>
    <h4> Total Books: {bookList().length}</h4>
    <h3 style={{ textAlign: "center", margin: 20}}>BOOK LIST</h3>
    <div className="card p-fluid datatable-editing-demo">
        <DataTable value={books} size="small" stripedRows paginator editMode="row" dataKey="id" onRowEditComplete={onRowEditComplete} responsiveLayout="scroll"
            paginatorTemplate="CurrentPageReport FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink RowsPerPageDropdown"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords}" rows={10} rowsPerPageOptions={[10,20,50]}
            paginatorLeft={paginatorLeft} paginatorRight={paginatorRight}>
            <Column field="title" header="Title" sortable ></Column>
            <Column field="author" header="Author" sortable></Column>
            <Column field="date" header="Date"  sortable></Column>
            <Column field="genre" header="Genre" sortable></Column>
            {/* <Column field="edit" header="Edit" rowEditor headerStyle={{ width: '10%', minWidth: '8rem' }}></Column> */}
            <Column field="action" header="Action" rowEditor headerStyle={{ width: '10%', minWidth: '8rem' }}  body={toggleClass}></Column>
        </DataTable>
    </div>


    <table className="table table-striped" style={{ marginTop: 100 }}>
      <thead>
        <tr>
          <th>Title</th>
          <th>Author</th>
          <th>Date</th>
          <th>Genre</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>{bookList()}</tbody>
    </table>
  </div>
);
}
