import React, { useRef, useState } from "react";
import { useNavigate } from "react-router";
import '../App.css';
import { Calendar } from 'primereact/calendar';
import { blue, blueGrey } from "@mui/material/colors";
import { Button } from 'primereact/button';
import { Checkbox } from "primereact/checkbox";


export default function Create() {
  const [form, setForm] = useState({
      title: "",
      author: "",
      date: "",
      genre: "",
  });

  const categories = [
    { genre: 'Action & Adventure', key: 'AA' },
    { genre: 'Adult', key: 'A' },
    { genre: 'Bibliography', key: 'B' },
    { genre: 'Comedy', key: 'C' },
    { genre: 'Drama', key: 'D' },
    { genre: 'Dystopian', key: 'Dy' },
    { genre: 'Fantasy', key: 'F' },
    { genre: 'Mystery', key: 'M' },
    { genre: 'Romance', key: 'R' },
    { genre: 'Satire', key: 'S' },
    { genre: 'Self Help', key: 'SH' },
    { genre: 'Thriller', key: 'T' },
    { genre: 'Young Adult', key: 'YA' },
  ];
  
  const [selectedCategories, setSelectedCategories] = useState([categories[1]]);

  const onCategoryChange = (e) => {
      let _selectedCategories = [...selectedCategories];
      if (e.checked)
          _selectedCategories.push(e.value);
      else 
          _selectedCategories = _selectedCategories.filter(category => category.key !== e.value.key);

      setSelectedCategories(_selectedCategories);
      updateForm({ genre: _selectedCategories });
  };

  const navigate = useNavigate();

  const [date, setDate] = useState('');
  const dateInputRef = useRef(null);
  
  // These methods will update the state properties.
  function updateForm(value) {
    setDate(value);
    return setForm((prev) => {
      return { ...prev, ...value };
  });
}

  // This function will handle the submission.
async function onSubmit(e) {
  e.preventDefault();
  var date = form.date;
    if (date == null || date == "Plan To Read") {
      date = 'Plan To Read';
    } else {
      date = JSON.stringify(date).slice(1, 11);
    }

  // When a post request is sent to the create url, we'll add a new book to the database.
  const newBook = {
    title: form.title,
    author: form.author,
    date: date,
    genre: form.genre,
  };

  //  const newBook = { ...form };

  await fetch("http://localhost:3000/book/add", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(newBook),
  })
  .catch(error => {
    window.alert(error);
    return;
  });

  setForm({ title: "", author: "", date: "", genre: "" });
  navigate("/");
}
 
 // This following section will display the form that takes the input from the user.
return (
  <div>
  <i className="pi pi-fw pi-arrow-circle-left" onClick={() => navigate("/")} style={{'fontSize':'2em','cursor':'pointer','marginLeft':50}}></i>
    <div style={{ marginTop: 50, marginLeft: 200, marginRight:200 }}>
      <h3>Add a New Book</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            className="form-control"
            id="title"
            value={form.title}
            onChange={(e) => updateForm({ title: e.target.value })}
          />
        </div>
       <div className="form-group">
         <label htmlFor="author">Author</label>
         <input
           type="text"
           className="form-control"
           id="author"
           value={form.author}
           onChange={(e) => updateForm({ author: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="date">Date</label>
         <div>
          <Calendar 
            type="date"
